import React, { useState } from 'react';
import './Login.css'
// Define a static list of users and passwords
const users = [

    { username: 'admin01', password: 'password1' },
    { username: 'admin2', password: 'password2' },
    { username: 'admin3', password: 'password3' },
];
export function Login() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    function handleLogin(event) {
        event.preventDefault();
        // Prevent the default form submission  
        // Look for a user with the entered username and password 
        const user = users.find(u => u.username === username && u.password === password);
        if (user) {

            // If a user was found, log them in   
            console.log('User logged in:', user.username);
            setError('');
            window.location = '/home'; // Clear any previous error messages    
        } else {

            // If no user was found, display an error message     
            console.log('Login failed');
            setError('Invalid username or password');
        }
    } return (
        <div className="nav" >
            <>
                <ul>
                    <h3>CODINGMARKET</h3>



                </ul>
            </>

            <div className="login-container">
                <form onSubmit={handleSubmit} className="login-form">        <h1 className="login-header">Login</h1>        <div className="form-group">          <label htmlFor="email">Email:</label>          <input            type="email"            id="email"            name="email"            placeholder="Enter your email address"            value={email}            onChange={handleEmailChange}          />        </div>        <div className="form-group">          <label htmlFor="password">Password:</label>          <input            type="password"            id="password"            name="password"            placeholder="Enter your password"            value={password}            onChange={handlePasswordChange}          />        </div>
                    <button type="submit" className="login-button">Log In</button>
                    {error && <div className="login-error">{error}</div>}
                </form>
            </div>
        </div>
    );
}
